import React from "react";

export default class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      valuesEntered: false,
      value: "",
      value2: "",
      value3: 8,
      value4: "",
      weekNum: 1,
      percentage1: 50
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.changeWeek = this.changeWeek.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }
  handleSubmit(event) {
    this.setState({ valuesEntered: true });
    event.preventDefault();
  }

  changeWeek(event) {
    var week = this.state.weekNum + 1;
    if (week === 5) {
      //control so weeks only go from 1 - 4
      week = 4;
    }

    switch (week) {
      case 1:
        this.setState({ percentage1: 50 });
        break;
      case 2:
        this.setState({ percentage1: 60 });
        break;
      case 3:
        this.setState({ percentage1: 70 });
        break;
      case 4:
        this.setState({ percentage1: 80 });
        break;
      default:
        this.setState({ percentage1: 50 });
    }
  }

  render() {
    if (!this.state.valuesEntered) {
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Name:
            <input
              name="value1"
              type="number"
              value={this.state.value1}
              onChange={this.handleInputChange}
            />
          </label>
          <label>
            Name:
            <input
              name="value2"
              type="number"
              value={this.state.value2}
              onChange={this.handleInputChange}
            />
          </label>
          <label>
            Name:
            <input
              name="value3"
              type="number"
              value={this.state.value3}
              onChange={this.handleInputChange}
            />
          </label>
          <label>
            Name:
            <input
              name="value4"
              type="number"
              value={this.state.value4}
              onChange={this.handleInputChange}
            />
          </label>
          <input type="submit" value="Submit" />
        </form>
      );
    } else {
      var bench = parseInt(this.state.value3);
      return (
        <li>
          <b1>{bench * this.state.percentage1}</b1>
        </li>
      );
    }
  }
}
